import { Card, CardContent, Grid, IconButton } from "@mui/material";
import React from "react";
import { format } from "date-fns";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import { useState } from "react";
import { Box, Typography } from "@mui/material";
import TaskIcon from "@mui/icons-material/Task";

const Post = ({ post }) => {
  //   const dateTime = new Date(post.dateTime);
  //   const date = dateTime.getDate();
  //   const month = dateTime.getMonth();
  //   const year = dateTime.getFullYear();

  //   const hour = dateTime.getHours();
  //   const minute = dateTime.getMinutes();

  const [posts, setPosts] = useState([{ id: 1, text: "post" }]);

  const handleDelete = (todoId) => {
    const updatedPost = posts.filter((todo) => todo.id !== todoId);
    setPosts(updatedPost);
  };

  return (
    <Card>
      <Box
        height="70vh"
        display="flex"
        alignItems="center"
        justifyContent="center"
        backgroundColor="white"
      >
        <Box textAlign="center">
          <TaskIcon
            sx={{
              fontSize: "50px",
            }}
          />
          <Box>
            <Typography>กรุณาเพิ่มข้อมูล</Typography>
          </Box>
        </Box>
      </Box>
      {posts.map((todo) => (
        <CardContent>
          <Grid container spacing={2}>
            <Grid item xs={1}>
              {format(new Date(post.dateTime), "HH:mm")}
            </Grid>
            <Grid item xs={2}>
              {format(new Date(post.dateTime), "dd  MMMM  yyyy")}
            </Grid>
            <Grid item xs={8}>
              {post.content}
            </Grid>
            <Grid item xs={1}>
              <IconButton onClick={() => handleDelete(todo.id)}>
                <DeleteOutlineIcon />
              </IconButton>
            </Grid>
          </Grid>
        </CardContent>
      ))}
    </Card>
  );
};

export default Post;
