import { Card, CardContent, Grid, IconButton } from "@mui/material";
import React from "react";
import { format } from "date-fns";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import { useState, useEffect } from "react";

const Post = ({ post }) => {
  //   const dateTime = new Date(post.dateTime);
  //   const date = dateTime.getDate();
  //   const month = dateTime.getMonth();
  //   const year = dateTime.getFullYear();

  //   const hour = dateTime.getHours();
  //   const minute = dateTime.getMinutes();

  const [posts, setPosts] = () => {
    const savedPosts = localStorage.getItem("posts");

    if (savedPosts) {
      return JSON.parse(savedPosts);
    } else {
      return [];
    }
  };

  function handleDeleteClick(content) {
    const removeItem = posts.filter((posts) => {
      return posts.content !== content;
    });

    setPosts(removeItem);
  }

  return (
    <Card>
      <CardContent>
        <Grid container spacing={2}>
          <Grid item xs={1}>
            {format(new Date(post.dateTime), "HH:mm")}
          </Grid>
          <Grid item xs={2}>
            {format(new Date(post.dateTime), "dd  MMMM  yyyy")}
          </Grid>
          <Grid item xs={8}>
            {post.content}
          </Grid>
          <Grid item xs={1}>
            <IconButton onClick={() => handleDeleteClick(post.content)}>
              <DeleteOutlineIcon />
            </IconButton>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

export default Post;
